INSERT INTO `wp_options` VALUES (1,'siteurl','http://expo15.theodi.dev','yes'),(2,'home','http://expo15.theodi.dev','yes'),(3,'blogname','関西オープンデータEXPO&#039;15','yes'),(4,'blogdescription','International Open Data Day 2015 Pre-Event','yes'),(5,'users_can_register','0','yes'),(6,'admin_email','miya@wpist.me','yes'),(7,'start_of_week','1','yes'),(8,'use_balanceTags','0','yes'),(9,'use_smilies','1','yes'),(10,'require_name_email','1','yes'),(11,'comments_notify','1','yes'),(12,'posts_per_rss','10','yes'),(13,'rss_use_excerpt','0','yes'),(14,'mailserver_url','mail.example.com','yes'),(15,'mailserver_login','login@example.com','yes'),(16,'mailserver_pass','password','yes'),(17,'mailserver_port','110','yes'),(18,'default_category','1','yes'),(19,'default_comment_status','closed','yes'),(20,'default_ping_status','closed','yes'),(21,'default_pingback_flag','','yes'),(22,'posts_per_page','10','yes'),(23,'date_format','Y年n月j日','yes'),(24,'time_format','g:i A','yes'),(25,'links_updated_date_format','Y年n月j日 g:i A','yes'),(26,'comment_moderation','','yes'),(27,'moderation_notify','1','yes'),(28,'permalink_structure','/archives/%post_id%','yes'),(29,'gzipcompression','0','yes'),(30,'hack_file','0','yes'),(31,'blog_charset','UTF-8','yes'),(32,'moderation_keys','','no'),(33,'active_plugins','a:14:{i:0;s:39:\"automatic-updater/automatic-updater.php\";i:1;s:39:\"content-bootstrap/content-bootstrap.php\";i:3;s:27:\"ichigen-san/ichigen-san.php\";i:4;s:13:\"kode/kode.php\";i:5;s:33:\"nginx-champuru/nginx-champuru.php\";i:6;s:36:\"ogp-generator/nskw-ogp-generator.php\";i:7;s:25:\"simple-map/simple-map.php\";i:8;s:29:\"simple-share/simple-share.php\";i:9;s:22:\"staticpress/plugin.php\";i:10;s:85:\"tinymce-templates-bootstrap-templates-1.2.0/tinymce-templates-bootstrap-templates.php\";i:11;s:77:\"tinymce-templates-shortcode-addon-1.0.0/tinymce-templates-shortcode-addon.php\";i:12;s:39:\"tinymce-templates/tinymce-templates.php\";i:13;s:41:\"wp-multibyte-patch/wp-multibyte-patch.php\";i:14;s:33:\"wp-total-hacks/wp-total-hacks.php\";}','yes'),(34,'category_base','','yes'),(35,'ping_sites','http://rpc.pingomatic.com/','yes'),(36,'advanced_edit','0','yes'),(37,'comment_max_links','2','yes'),(38,'gmt_offset','9','yes'),(39,'default_email_category','1','yes'),(40,'recently_edited','','no'),(41,'template','twentythirteen','yes'),(42,'stylesheet','expo15_theodi_jp','yes'),(43,'comment_whitelist','1','yes'),(44,'blacklist_keys','','no'),(45,'comment_registration','','yes'),(46,'html_type','text/html','yes'),(47,'use_trackback','0','yes'),(48,'default_role','subscriber','yes'),(49,'db_version','31535','yes'),(50,'uploads_use_yearmonth_folders','1','yes'),(51,'upload_path','','yes'),(52,'blog_public','1','yes'),(53,'default_link_category','2','yes'),(54,'show_on_front','posts','yes'),(55,'tag_base','','yes'),(56,'show_avatars','1','yes'),(57,'avatar_rating','G','yes'),(58,'upload_url_path','','yes'),(59,'thumbnail_size_w','150','yes'),(60,'thumbnail_size_h','150','yes'),(61,'thumbnail_crop','1','yes'),(62,'medium_size_w','300','yes'),(63,'medium_size_h','300','yes'),(64,'avatar_default','mystery','yes'),(65,'large_size_w','1024','yes'),(66,'large_size_h','1024','yes'),(67,'image_default_link_type','file','yes'),(68,'image_default_size','','yes'),(69,'image_default_align','','yes'),(70,'close_comments_for_old_posts','','yes'),(71,'close_comments_days_old','14','yes'),(72,'thread_comments','1','yes'),(73,'thread_comments_depth','5','yes'),(74,'page_comments','','yes'),(75,'comments_per_page','50','yes'),(76,'default_comments_page','newest','yes'),(77,'comment_order','asc','yes'),(78,'sticky_posts','a:0:{}','yes'),(79,'widget_categories','a:1:{s:12:\"_multiwidget\";i:1;}','yes'),(80,'widget_text','a:3:{i:6;a:3:{s:5:\"title\";s:85:\"関西オープンデータEXPO\'15、ご来場いただき有難うございました\";s:4:\"text\";s:608:\"<p>International Open Data Day 2015 の関西プレイベントとして開催した「関西オープンデータEXPO\'15」、おかげさまで盛会にて終了いたしました！</p>\r\n<p>2月21日（土）はぜひとも各地のイベントにご参加いただけたらと思います。詳細は下記参照ください！</p>\r\n\r\n<h3>関西オープンデータEXPO\'15 で使用したスライドやウェブページ</h3>\r\n<p>当日のプレゼンで発表したスライドやウェブページ等、<a href=\"reference\">こちらのページ</a>からご覧いただくことができます。</p>\";s:6:\"filter\";b:0;}i:9;a:3:{s:5:\"title\";s:39:\"2月21日（土）の各地開催情報\";s:4:\"text\";s:7294:\"<p>2月21日（土）に各地で開催される Interntational Open Data Day、今回の「関西オープンデータEXPO\'15」で発表された実施主体につき、一覧でご紹介します（発表順、エリア名は<a href=\"http://okfn.jp/2014/12/08/1208odd2015/\" target=\"_blank\"> OKFJ が集計した「オープンデータデイ2015 開催地登録フォーム」に基づく一覧</a>に登録された名称を使用）。</p>\r\n\r\n<h3>岐阜</h3>\r\n<table class=\"table\">\r\n<tr><th>実施主体</th><td>Code for Gifu</td></tr>\r\n<tr><th>会場</th><td>岐阜市中央青少年会館（旧明徳小学校）<br />\r\n（岐阜市明徳町11）</td></tr>\r\n<tr><th>時間</th><td>11:00-17:00</td></tr>\r\n<tr><th>内容</th><td>「続・柳ヶ瀬100年史」柳ヶ瀬まち歩き（マッピングパーティー）</td></tr>\r\n<tr><th>URL</th><td><a href=\"https://www.facebook.com/events/746374622123393/\" target=\"_blank\">https://www.facebook.com/events/746374622123393/</a></td></tr>\r\n</table>\r\n\r\n<h3>奈良県生駒市</h3>\r\n<table class=\"table\">\r\n<tr><th>実施主体</th><td>生駒市<br />CODE for IKOMA </td></tr>\r\n<tr><th>会場</th><td>生駒市コミュニティーセンター<br />\r\n（生駒市元町1丁目6-12） </td></tr>\r\n<tr><th>時間</th><td>10:00-16:30</td></tr>\r\n<tr><th>内容</th><td>「iko mama papa アプリ開発提案プロジェクト」子育てアプリ アイデアワークショップ Vol.2</td></tr>\r\n<tr><th>URL</th><td><a href=\"http://code4ikoma.org/?q=events\" target=\"_blank\">http://code4ikoma.org/?q=events</a></td></tr>\r\n</table>\r\n\r\n<h3>奈良市</h3>\r\n<table class=\"table\">\r\n<tr><th>実施主体</th><td>Code for Nara</td></tr>\r\n<tr><th>会場</th><td>奈良女子大学 奈良町セミナーハウス\r\n<br />（奈良市薬師堂町12）</td></tr>\r\n<tr><th>時間</th><td>13:00-18:00</td></tr>\r\n<tr><th>内容</th><td>第2回奈良オープンデータソン（OpenStreetMap）</td></tr>\r\n<tr><th>URL</th><td><a href=\"http://d8c0498000e8ca2f89ceb81f71.doorkeeper.jp/events/20396\" target=\"_blank\">http://d8c0498000e8ca2f89ceb81f71.doorkeeper.jp/events/20396</a></td></tr>\r\n</table>\r\n\r\n<h3>京都市</h3>\r\n<table class=\"table\">\r\n<tr><th>実施主体</th><td>オープンデータ京都実践会</td></tr>\r\n<tr><th>会場</th><td>浄土宗 くろ谷 永運院\r\n<br />（京都市左京区黒谷町121）</td></tr>\r\n<tr><th>時間</th><td>10:00-17:00</td></tr>\r\n<tr><th>内容</th><td>京都まちあるきオープンデータソン（OpenStreetMap、Wikipedia）</td></tr>\r\n<tr><th>URL</th><td><a href=\"http://opendata-kyoto.doorkeeper.jp/events/20597\" target=\"_blank\">http://opendata-kyoto.doorkeeper.jp/events/20597</a></td></tr>\r\n</table>\r\n\r\n<h3>滋賀</h3>\r\n<table class=\"table\">\r\n<tr><th>実施主体</th><td>Code for Shiga / Biwako</td></tr>\r\n<tr><th>会場</th><td>滋賀県立芸術劇場 びわ湖ホール\r\n<br />（大津市打出浜15-1）</td></tr>\r\n<tr><th>時間</th><td>11:00-17:00</td></tr>\r\n<tr><th>内容</th><td>「ラ・フォル・ジュルネ びわ湖 2015」「守山ほたるパーク＆ウォーク」アプリ開発ワークショップ</td></tr>\r\n<tr><th>URL</th><td><a href=\"http://opendata.shiga.jp/iodd2015/\" target=\"_blank\">http://opendata.shiga.jp/iodd2015/</a></td></tr>\r\n</table>\r\n\r\n<h3>徳島県</h3>\r\n<table class=\"table\">\r\n<tr><th>実施主体</th><td>Code for Tokushima 準備委員会</td></tr>\r\n<tr><th>会場</th><td>フレアとくしま 研修室１、２<br />（徳島市山城町東浜傍示1）</td></tr>\r\n<tr><th>時間</th><td>11:00-17:00</td></tr>\r\n<tr><th>内容</th><td>オープンデータアイデアソン「ママとICTで住みよい街を」</td></tr>\r\n<tr><th>URL</th><td><a href=\"https://www.facebook.com/events/365863140264403/\" target=\"_blank\">https://www.facebook.com/events/365863140264403/</a></td></tr>\r\n</table>\r\n\r\n<h3>堺市</h3>\r\n<table class=\"table\">\r\n<tr><th>実施主体</th><td>Code for Sakai</td></tr>\r\n<tr><th>会場</th><td>大仙公園内の日本庭園<br />（堺市堺区大仙中町）</td></tr>\r\n<tr><th>時間</th><td>10:00-17:00</td></tr>\r\n<tr><th>内容</th><td>「堺市の魅力再発見！まちあるきワークショップ〜オープンデータを使ったオリジナル観光ガイドを作ろう〜」（マッピングパーティー）</td></tr>\r\n<tr><th>URL</th><td><a href=\"http://peatix.com/event/72435\" target=\"_blank\">http://peatix.com/event/72435</a></td></tr>\r\n</table>\r\n\r\n<h3>兵庫県 / 神戸市</h3>\r\n<table class=\"table\">\r\n<tr><th>実施主体</th><td>Code for Hyogo<br />Code for Kobe</td></tr>\r\n<tr><th>会場</th><td>兵庫県庁第2号館 1階視聴覚室<br />（神戸市中央区下山手通5丁目10-1）</td></tr>\r\n<tr><th>時間</th><td>10:30-17:00</td></tr>\r\n<tr><th>内容</th><td>データ可視化アイデアワークショップ</td></tr>\r\n<tr><th>URL</th><td><a href=\"http://codeforkobe-3.peatix.com/\" target=\"_blank\">http://codeforkobe-3.peatix.com/</a></td></tr>\r\n</table>\r\n\r\n<h3>和歌山県</h3>\r\n<table class=\"table\">\r\n<tr><th>実施主体</th><td>Code for Wakayama</td></tr>\r\n<tr><th>会場</th><td>和歌山県立情報交流センターBig-U<br />（田辺市新庄町3352-9）</td></tr>\r\n<tr><th>時間</th><td>10:00-17:00</td></tr>\r\n<tr><th>内容</th><td>「オープンデータを作ろう！」「マッピングパーティin 南紀田辺！」</td></tr>\r\n<tr><th>URL</th><td><a href=\"https://www.facebook.com/pages/International-Open-Data-Day-2015-in-Wakayama/423340991176440\" target=\"_blank\">https://www.facebook.com/pages/International-Open-Data-Day-2015-in-Wakayama/423340991176440</a></td></tr>\r\n</table>\r\n\r\n<h3>丹波</h3>\r\n<table class=\"table\">\r\n<tr><th>実施主体</th><td>OpenData@丹波実行委員会</td></tr>\r\n<tr><th>会場</th><td>株式会社ご近所<br />（兵庫県丹波市春日町中山192番地1）</td></tr>\r\n<tr><th>時間</th><td>9:00-16:00</td></tr>\r\n<tr><th>内容</th><td>「私の好きな丹波プロジェクト」風景写真の投稿イベント</td></tr>\r\n</table>\r\n\r\n<h3>名古屋市</h3>\r\n<table class=\"table\">\r\n<tr><th>実施主体</th><td>オープンデータ東海 / Code for TOKAI<br />Code for NAGOYA<br />\r\n名古屋大学未来社会創造機構社会イノベーションデザイン学センター（SIDC）</td></tr>\r\n<tr><th>会場</th><td>名古屋大学 IB電子情報館 南棟295講義室<br />（名古屋市千種区不老町1）</td></tr>\r\n<tr><th>時間</th><td>10:00-18:00</td></tr>\r\n<tr><th>内容</th><td>「公共交通オープンデータ」（アイデアソン、ハッカソン）</td></tr>\r\n<tr><th>URL</th><td><a href=\"http://opendata-nagoya2015.peatix.com/\" target=\"_blank\">http://opendata-nagoya2015.peatix.com/</a></td></tr>\r\n</table>\r\n\r\n<h3>大阪市</h3>\r\n<table class=\"table\">\r\n<tr><th>実施主体</th><td>大阪イノベーションハブ<br />ODI Osaka</td></tr>\r\n<tr><th>会場</th><td>大阪イノベーションハブ<br />（大阪市北区大深町3-1 グランフロント大阪 ナレッジキャピタルタワーC 7階）</td></tr>\r\n<tr><th>時間</th><td>10:00-18:30</td></tr>\r\n<tr><th>内容</th><td>ハッカソン・データソン</td></tr>\r\n<tr><th>URL</th><td><a href=\"https://www.facebook.com/events/798061366939606/\" target=\"_blank\">https://www.facebook.com/events/798061366939606/</a></td></tr>\r\n</table>\";s:6:\"filter\";b:0;}s:12:\"_multiwidget\";i:1;}','yes'),(81,'widget_rss','a:0:{}','yes'),(82,'uninstall_plugins','a:0:{}','no'),(83,'timezone_string','','yes'),